import React, { useState } from "react";

function CreateArea(props) {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [notes, setNotes] = useState([]);

    function handleTitleChange(event){
        const titleText = event.target.value;
        setTitle(titleText);
        
    }

    function handleContentChange(event){
        const contentText = event.target.value;
        setContent(contentText);
    }
    

    function addNote(){
        const note = {
            title: title,
            content: content
        }

        setNotes(note);
        console.log(notes);
    }
     


  return (
    <div>
      <form>
        <input name="title" placeholder="Title" value={title} onChange={handleTitleChange} />
        <textarea name="content" placeholder="Take a note..." rows="3" value={content} onChange={handleContentChange} />
        <button onClick={() => addNote(props.notes)}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
